import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, Image, ImageBackground, Dimensions } from 'react-native';
import Icon from 'react-native-vector-icons/FontAwesome';
import { useFonts } from 'expo-font'


export default function App() {
  const [fontsLoaded] = useFonts({
    'Allison': require('./assets/fonts/Allison.ttf'),
  })
  const styles = StyleSheet.create({
    container: {
      flex: 1,
    },
    image: {
      flex: 1,
      justifyContent: 'center',
      resizeMode: "cover",
    },
    logoText: {
      position: 'relative',
      color: 'white', 
      fontFamily: 'Allison', 
      fontSize: 100, 
      zIndex: 3, 
      elevation: 3, 
      textAlign: 'center',
      top: -Dimensions.get('window').height / 6,
    },
    button:{
      width: 200,
      alignSelf: 'center',
    }
  })

  
  

  return (
    <View style={styles.container}>
      <StatusBar style="auto" />
      {/* <Image source={Icon.logo} style={{width: 100, height: 100}} /> */}
      <ImageBackground source="https://14thlane.ro/files/pages/1/OurStory2.webp" resizeMode="cover" style={styles.image}>
        <Text style={styles.logoText}>FoodFinder</Text>
        <View style={styles.button}>
          <Icon.Button name="facebook" mode="contained" backgroundColor="#3b5998">
            <Text style={{color:'white', textAlign:'center', justifyContent: 'center', alignItems: 'center',}}>
              Login with Facebook
            </Text>
          </Icon.Button>
        </View>
      </ImageBackground>
    </View>
    
  );
}
